import Card from './components/Card';
import Header from './components/Header';
import Drawer from './components/Drawer';
import React from 'react';

function App() {
  const [cartOpened, setCartOpened] = React.useState(false);
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');

  React.useEffect(() => {
    fetch ('https://6387382ad9b24b1be3e9cb49.mockapi.io/items').then((res) =>{
      return res.json();
    }).then((json) => {
      setItems (json);
    });
  }, []);

  const onAddToCart = (obj) => {
    setCartItems(prev => [...prev, obj]);
  };

  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div className="wrapper clear">
      {cartOpened && <Drawer items={cartItems} onCloseCart={() => setCartOpened(false)}/>}
      <Header onClickCart={() => setCartOpened(true)}/>
      <div className="content p-40">
        <div className= "d-flex flex-wrap align-center justify-between mb-20 mr-15 ml-15">
          <h1>{searchValue ? `Поиск по запросу: "${searchValue}"` : 'Все кроссовки'}</h1>
          <div className="search-block">
            <div className='search-block__body'>
              <img src="img/search/search.svg" alt="search" />
              <input onChange={onChangeSearchInput} value={searchValue} placeholder="Поиск..." />
              {searchValue && <img 
                onClick={() => setSearchValue('')}
                src="img/search/clear.svg" 
                alt="clear"
                className="clear cu-p" 
                src="/img/drawer/btn-remove.svg" 
                alt="Remove" 
                />}
            </div>
          </div>
        </div>
        <div className="sneakers d-flex flex-wrap">
          {items
          .filter((i))
          .map((item, index) => (
            <Card 
              key={index}
              imgUrl={item.imgUrl}
              title={item.name}
              price={item.price}
              onPlus = {(obj) => onAddToCart(obj)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
