import styles from './Card.modules.scss';
import React from 'react';

function Card(props) {
  const [isAdded, setIsAdded] = React.useState(false);

  const onClickPlus = () => {
    setIsAdded(true);
  };

   return (
      <div className="card">
      <div className="favorite">
        <button className='unliked__btn' onClick={props.onClickFavorite}>
          <img src="/img/card/icons/heart-unliked.svg" alt="unliked" />
        </button>
      </div>
      <img width={133} height={112} src={props.imgUrl} alt="Sneakers" />
      <h5>{props.title}</h5>
      <div className="card-body d-flex justify-between align-center">
        <div className="d-flex flex-column">
          <span>Цена:</span>
          <b>{props.price} руб.</b>
        </div>
        <button className="button" >
          <img 
            className={styles.plus}
            width={11} 
            height={11} 
            src={isAdded ? "/img/card/icons/btn-checked.svg" : "/img/card/icons/btn-plus.svg"} 
            alt="Plus" 
            onClick={onClickPlus}
            />
        </button>
      </div>
    </div>
   );
}

export default Card;
