import Card from '../components/Card';

function Liked({items, onAddToLiked, onAddToCart}) {
   return (
      <div className="content p-40">
         <div className= "d-flex flex-wrap align-center  mb-20 mr-15 ml-15">
            <img width={35} height={35} src="/img/liked/back.svg" alt="back" className='mr-10'/>
            <h1 className='ml-10'>Мои закладки</h1>
         </div>
         <div className="sneakers d-flex flex-wrap">
            {
               items.lenght > 0 ?
                  <div>
                     {items
                        .map((item, index) => (
                           <Card 
                           key={index}
                           onPlus = {(obj) => onAddToCart(obj)}
                           onLiked ={(obj) => onAddToLiked(obj)}
                           Liked={true}
                           {...item}
                           />
                     ))}
                  </div>
               :
               <div className="cartEmpty d-flex align-center justify-center flex-column flex">
                  <img className="mb-20" width={120} height={120} src="/img/liked/smail.png" alt="Smail"/>
                  <h2>Закладок нет :(</h2>
                  <p className="opacity-6">Вы ничего не добавляли в закладки</p>
                  <button className="greenButton" onClick={onCloseCart}>
                     <img 
                        src="/img/drawer/arrow.svg" 
                        alt="Arrow"
                     />
                     Вернуться назад
                  </button>
               </div>
            } 
         </div>
      </div>
   )
}

export default Liked;
