import styles from './Card.modules.scss';
import React from 'react';

function Card(props) {
  const [isAdded, setIsAdded] = React.useState(false);

  const onClickPlus = () => {
    setIsAdded(!isAdded);
  };

  const [isLiked, setLiked] = React.useState(false);

  const onClickLikes = () => {
    setLiked(!isLiked);
  };

   return (
      <div className="card">
      <div className="favorite">
        <img 

          src={isLiked ? "/img/card/icons/heart-unliked.svg" : "/img/card/icons/heart-liked.svg"} 
          alt="unliked" 
          onClick={onClickLikes}
          />
      </div>
      <img width={133} height={112} src={props.imgUrl} alt="Sneakers" />
      <h5>{props.title}</h5>
      <div className="card-body d-flex justify-between align-center">
        <div className="d-flex flex-column">
          <span>Цена:</span>
          <b>{props.price} руб.</b>
        </div>
        <img 
          className={styles.iconBtn} 
          src={isAdded ? "/img/card/icons/btn-checked.svg" : "/img/card/icons/btn-plus.svg"} 
          alt="Plus" 
          onClick={onClickPlus}
          />
      </div>
    </div>
   );
}

export default Card;
