// };

   return (
      <div className="card">
      <div className="favorite">
        <img src="/img/card/icons/heart-unliked.svg" alt="unliked" />
      </div>
      <img width={133} height={112} src={props.imgUrl} alt="Sneakers" />
      <h5>{props.title}</h5>
      <div className="card-body d-flex justify-between align-center">
        <div className="d-flex flex-column">
          <span>Цена:</span>
          <b>{props.price} руб.</b>
        </div>
        <button className="button" onClick={onClickPlus}>
          <img width={11} height={11} src="/img/card/icons/plus.svg" alt="Plus" />
        </button>
      </div>
    </div>
   );
}

export default Card;
