import Card from './components/Card';
import Header from './components/Header';
import Drawer from './components/Drawer';
import React from 'react';

function App() {
  const [cartOpened, setCartOpened] = React.useState(false);
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);

React.useEffect(() => {
  fetch ('https://6387382ad9b24b1be3e9cb49.mockapi.io/items').then((res) =>{
    return res.json();
  }).then((json) => {
    setItems (json);
  });
}, []);

  return (
    <div className="wrapper clear">
      {cartOpened && <Drawer items={cartItems} onCloseCart={() => setCartOpened(false)}/>}
      <Header onClickCart={() => setCartOpened(true)}/>
      <div className="content p-40">
        <div className= "d-flex align-center justify-between mb-20 mr-15 ml-15">
          <h1>Все кроссовки</h1>
          <div className="search-block">
            <img src="img/search/search.svg" alt="search" />
            <input placeholder="Поиск..." />
          </div>
        </div>
        <div className="sneakers d-flex flex-wrap">
          {items.map((obj) => (
            <Card 
              imgUrl={obj.imgUrl}
              title={obj.name}
              price={obj.price}
              onFavorite = {() => console.log('Добавили в за')}
              onPlus = {() => console.log('Нажали плюс')}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
