import React from "react";
import Info from "./Info";
import AppContext from "../context";

function Drawer({onCloseCart, onRemove, items = []}) {
  const {setCartItems} = React.useContext(AppContext);
  const [isOrderComplete, setIsOrderComplete] = React.useState(false);

const onClickOrder = () => {
  setIsOrderComplete(true);
  setCartItems([]);
}

  return (
    <div className="overlay">
      <div className="drawer">
      <h2 className="mb-30 d-flex justify-between align-center">
        Корзина
        <img 
          className="cu-p" 
          src="/img/drawer/btn-remove.svg" 
          alt="Remove" 
          onClick={onCloseCart}
          />
      </h2>
      {
        items.length > 0 ? 
        (<>
          <div className="items flex">
            {items.map((obj) => (
              <div key={obj.id} className="cartItem d-flex align-center mb-20">
                <div className="cartItemImg" style={{ backgroundImage: `url(${obj.imgUrl})` }}>
                </div>
                <div className="mr-12 flex">
                  <p className="mb-5">{obj.title}</p>
                  <b>{obj.price} руб.</b>
                </div>
                <img className="removeBtn" onClick={() => onRemove(obj.id)} src="/img/drawer/btn-remove.svg" alt="Remove" />
              </div>
            ))}
          </div>
          <div className="cartTotalBlock">
            <ul>
              <li>
                <span>Итого:</span>
                <div></div>
                <b>21 498 руб.</b>
              </li>
              <li>
                <span>Налог 5%: </span>
                <div></div>
                <b>1074 руб.</b>
              </li>
            </ul>
            <button className="greenButton" onClick={onClickOrder}>
              Оформить заказ
              <img src="/img/drawer/arrow.svg" />
            </button>
          </div>
        </>)
        : 
        (<Info 
          title={isOrderComplete ? "Заказ оформлен!" : "Корзина пустая"} 
          description={isOrderComplete ? "Ваш заказ #18 скоро будет передан курьерской доставке" : "Добавьте хотя бы одну пару кроссовок, чтобы сделать заказ."} 
          image="/img/drawer/empty-cart.jpeg" />)
      }      
      </div>
    </div>
   );
}

export default Drawer;
