import Header from './components/Header';
import Drawer from './components/Drawer';
import {Route, Routes, Navigate} from 'react-router-dom';
import React from 'react';
import axios from 'axios';

import Home from './pages/Home.jsx'
import Liked from './pages/Liked.jsx';

function App() {
  const [cartOpened, setCartOpened] = React.useState(false);
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [isLiked, setLiked] = React.useState([]);


  React.useEffect(() => {
    axios.get('https://6387382ad9b24b1be3e9cb49.mockapi.io/items').then((res) =>{
      setItems (res.data);
    });
    axios.get('https://6387382ad9b24b1be3e9cb49.mockapi.io/cart').then((res) =>{
      setCartItems (res.data);
    });
    axios.get('https://6387382ad9b24b1be3e9cb49.mockapi.io/liked').then((res) =>{
      setLiked (res.data);
    });
  }, []);

  const onAddToCart = (obj) => {
    try {
      if (cartItems.find(item => Number(item.id) === Number(obj.id))){
        setCartItems(prev => prev.filter(item => Number(item.id) !== obj.id))
      } else {
        axios.post(`https://6387382ad9b24b1be3e9cb49.mockapi.io/cart`, obj);
        setCartItems((prev) => [...prev, obj]);    
      }
    }catch (error) {

    }
  };

  const onRemoveItem = (id) => {
    axios.delete(`https://6387382ad9b24b1be3e9cb49.mockapi.io/cart/${id}`);
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  };

  const onAddToLiked = async(obj) => {
    try {
      if (isLiked.find(likObj => likObj.id === obj.id)){
        axios.delete(`https://6387382ad9b24b1be3e9cb49.mockapi.io/liked/${obj.id}`);
        setLiked((prev) => prev.filter((item) => item.id !== obj.id));
      } else {
          const {data} = await axios.post(`https://6387382ad9b24b1be3e9cb49.mockapi.io/liked`, obj);
          setLiked((prev) => [...prev, data]);  
      }
    } catch (error) {
      alert ('Не удалось добавить в избранные');
    }
  };

  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div className="wrapper clear">
      {cartOpened && <Drawer items={cartItems} onCloseCart={() => setCartOpened(false)} onRemove={onRemoveItem}/>}
      <Header onClickCart={() => setCartOpened(true)}/>
      <Routes>
        <Route path="/" element={<Home 
            items={items} 
            searchValue={searchValue} 
            setSearchValue={setSearchValue} 
            onChangeSearchInput={onChangeSearchInput} 
            onAddToLiked={onAddToLiked}
            onAddToCart={onAddToCart}
          />}>
        </Route>
        <Route path="/liked" element={<Liked 
          items={isLiked}
          onAddToLiked={onAddToLiked}
          onAddToCart={onAddToCart}
        />}> 
        </Route>
      </Routes>

    </div>
  );
}

export default App;
