import styles from './Card.modules.scss';
import React from 'react';
import axios from 'axios';

function Card({imgUrl, title, price, onPlus}) {
  const [isAdded, setIsAdded] = React.useState(false);

  const onClickPlus = () => {
    setIsAdded(!isAdded);
    onPlus({imgUrl, title, price});
  };

  const [isLiked, setLiked] = React.useState(false);

  const onClickLikes = () => {
    setLiked(!isLiked);
  };


   return (
      <div className="card">
      <div className="favorite">
        <img 
          className={styles.iconBtn} 
          src={isLiked ? "/img/card/icons/heart-unliked.svg" : "/img/card/icons/heart-liked.svg"} 
          alt="unliked" 
          onClick={onClickLikes}
          />
      </div>
      <img width={133} height={112} src={imgUrl} alt="Sneakers" />
      <h5>{title}</h5>
      <div className="card-body d-flex justify-between align-center">
        <div className="d-flex flex-column">
          <span>Цена:</span>
          <b>{price} руб.</b>
        </div>
        <img 
          className={styles.iconBtn} 
          src={isAdded ? "/img/card/icons/btn-checked.svg" : "/img/card/icons/btn-plus.svg"} 
          alt="Plus" 
          onClick={onClickPlus}
          />
      </div>
    </div>
   );
}

export default Card;
